import React, { useState,useEffect } from "react";
import {

  Button,
  Form,
  Modal,
} from "react-bootstrap";
import FormContainer from "../components/FormContainer";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useDispatch, useSelector } from "react-redux";
import {editSize, getSizeDetails} from '../redux/actions/sizeActions';

const EditSize = ({id,history}) => {

  const dispatch = useDispatch();
  const sizeDetails = useSelector((state) => state.sizeDetailsReducer);
  const { size: sizeInfo, loading, error } = sizeDetails;

  const sizeUpdate = useSelector((state) => state.sizeUpdateReducer);
  const { success, error: errorUpdate } = sizeUpdate;

  const [size, setSize] = useState({
    size: "",
  });
  const [message, setMessage] = useState("Error")


  useEffect(() => {
     dispatch(getSizeDetails(id));  
     if(sizeInfo){
        setSize(sizeInfo);
     }
     //checks if update product success
     if(success){

        setMessage("Product updated successfully")
        setTimeout(()=>{
          dispatch({type:SIZE_UPDATE_RESET})
          history.push('/admin/api/get')
        },1500)
     }


  },[dispatch,id]);



  return (
    <>
      <Modal.Dialog>
        <Modal.Header>
          <Modal.Title>Update API specifications</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <FormContainer>
            <Form>
              <Form.Group controlId="size" className="mt-3">
                <Form.Label>Size</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="size e.g S/M/L.."
                  value={sizeInfo.size}
                  onChange={(e) => setSize({ size: e.target.value })}
                ></Form.Control>
              </Form.Group>
            </Form>
          </FormContainer>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Go Back</Button>
          <Button variant="primary" onClick={()=>dispatch(editSize(size))}>Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </>
  );
};

export default EditSize;
