import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/Message";
import { Link } from "react-router-dom";
import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from "react-bootstrap";
import { addToCart ,removeFromCart } from "../redux/actions/cartActions";

import EmptyCartSvg from '../assets/EmptyCartSvg'


import { CART_RESET } from "../redux/types";

const CartScreen = ({ match, location, history }) => {

  const productId = match.params.id;

  const qty = location.search ? Number(location.search.split("=")[1]) : 1;

  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;


  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler = (id) => {
       dispatch(removeFromCart(id))
  };

  const checkoutHandler = () => {
      history.push('/login?redirect=shipping')
  };

  return (

    <Row style={{display:'flex',justifyContent:'center'}}>
      <Col md={8}>
        <h1>Shopping Cart</h1>

        {cartItems.length >0 && <Button variant="outline-danger" onClick={()=>{dispatch({type:CART_RESET}) && localStorage.setItem('cartItems',JSON.stringify([]))}}>Reset cart</Button>}

        {cartItems.length === 0 ? (
          <div style={{width:'100%',display:'grid',placeContent:'center'}} >
            <Message text={"Your cart is empty"} variant="info"/>
              <EmptyCartSvg width='300' height='300'/>
          </div>
        ) : (
          <ListGroup variant="flush" style={{marginTop:10}}>
            {cartItems.map((item) => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>{item.price}</Col>
                  <Col md={2}>
                    <Form.Control
                      as="select"
                      value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.product, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                  </Col>
                  <Col md={2}>
                    <Button
                      type="button"
                      variant="light"
                      onClick={() => removeFromCartHandler(item.product)}
                    >
                      <i className="fas fa-trash"></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      {cartItems.length > 0 &&
        <Col md={4}>
        <Card>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>
                SUBTOTAL ({cartItems.reduce((acc, item) => acc + item.qty, 0)})
                ITEMS
              </h2>
              ${cartItems.reduce((acc, item) => acc + (item.qty*item.price),0).toFixed(2)}
            </ListGroup.Item>
            <ListGroup.Item>
                <Button type='button' className='btn-block' disabled={cartItems.length===0} onClick={checkoutHandler}>Proceed to checkout</Button>
            </ListGroup.Item>
          </ListGroup>
        </Card>
      </Col>
      }
    </Row>
  );
};

export default CartScreen;
