import React, { useState,useEffect } from "react";
import {

  Button,
  Form,
  Modal,
} from "react-bootstrap";
import FormContainer from "../components/FormContainer";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useDispatch, useSelector } from "react-redux";
import {getSizeDetails} from '../redux/actions/sizeActions';

const EditSize = ({id}) => {
    console.log('id',match.params.id);
  const dispatch = useDispatch();
  const sizeDetails = useSelector((state) => state.sizeDetailsReducer);
  const { size: sizeInfo, loading, error } = sizeDetails;

  console.log(sizeInfo);

  const [size, setSize] = useState({
    size: "",
  });

  useEffect(() => {
      getSizeDetails()
  },[])

  return (
    <>
      <Modal.Dialog>
        <Modal.Header>
          <Modal.Title>Update API specifications</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <FormContainer>
            <Form>
              <Form.Group controlId="size" className="mt-3">
                <Form.Label>Size</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="size e.g S/M/L.."
                  value={size.size}
                  onChange={(e) => setSize({ size: e.target.value })}
                ></Form.Control>
              </Form.Group>
            </Form>
          </FormContainer>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary">Go Back</Button>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </>
  );
};

export default EditSize;
