import React ,{useState} from "react";
import { Table, Button, Col, Row ,InputGroup,FormControl,Modal} from "react-bootstrap";

import { LinkContainer } from "react-router-bootstrap";
import ModalTest from './ModalTest';

const TableData = ({ array, deleteHandler,name }) => {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
  let obj = array[0];




  return (
    <>
      <Row className="d-flex justify-content-center mb-4" md="auto">
        <Col>
          <InputGroup>
            <FormControl
              placeholder="Search"
              onChange={(e) => null}
            />
          </InputGroup>
        </Col>
      </Row>
      <Table striped bordered hover responsive className="table-sm">
        <thead>
          <tr>
            {obj &&
              Object.keys(obj)
                .filter((key) => key !== "__v")
                .map((key, index) => <th key={index}>{key}</th>)}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {array &&
            array?.map((item) => (
              <tr key={item._id}>
                {Object.values(item)
                  .filter((val) => val !== 0)
                  .map((val, idx) => (
                    <td key={idx}>{val}</td>
                  ))}

                <td>
                  <Col style={{ display: "flex" }}>

                      <Button className="btn-sm" variant="light"
                      onClick={()=>Example(show)}
                      >
                        <i className="fas fa-edit"></i>
                      </Button>

                    <Button
                      className="btn-sm"
                      variant="danger"
                      style={{ marginLeft: "10px" }}
                      onClick={() => deleteHandler(item._id)}
                    >
                      <i className="fas fa-trash"></i>
                    </Button>
                  </Col>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>
    </>
  );
};

export default TableData;


const Example = () => {
  const [show, setShow] = useState(true);

  const handleClose = () => setShow(false);


  return (

    <>
     {  console.log('modal')}

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
